import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Search, Filter, Flower, Star } from 'lucide-react';
import { perfumeAPI } from '../services/api';

const PerfumeList = () => {
  const [perfumes, setPerfumes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedPrice, setSelectedPrice] = useState('');
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    loadCategories();
    loadPerfumes();
  }, []);

  const loadCategories = async () => {
    try {
      const response = await perfumeAPI.getCategories();
      setCategories(response.data);
    } catch (error) {
      console.error('Ïπ¥ÌÖåÍ≥†Î¶¨ Î°úÎìú Ïã§Ìå®:', error);
    }
  };

  const loadPerfumes = async () => {
    setLoading(true);
    try {
      const params = {};
      if (selectedCategory) params.category = selectedCategory;
      if (selectedPrice) params.price_range = selectedPrice;
      
      const response = await perfumeAPI.getPerfumes(params);
      setPerfumes(response.data);
    } catch (error) {
      console.error('Ìñ•Ïàò Î™©Î°ù Î°úÎìú Ïã§Ìå®:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadPerfumes();
  }, [selectedCategory, selectedPrice]);

  const filteredPerfumes = perfumes.filter(perfume =>
    perfume.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    perfume.brand.toLowerCase().includes(searchTerm.toLowerCase()) ||
    perfume.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const getCategoryColor = (category) => {
    const colors = {
      floral: 'bg-pink-100 text-pink-800',
      woody: 'bg-amber-100 text-amber-800',
      fresh: 'bg-blue-100 text-blue-800',
      oriental: 'bg-purple-100 text-purple-800',
      citrus: 'bg-green-100 text-green-800'
    };
    return colors[category] || 'bg-gray-100 text-gray-800';
  };

  const getCategoryIcon = (category) => {
    const icons = {
      floral: 'üå∏',
      woody: 'üå≥',
      fresh: 'üåä',
      oriental: 'üïå',
      citrus: 'üçã'
    };
    return icons[category] || 'üí´';
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-primary-50 to-secondary-50 py-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600 mx-auto"></div>
            <p className="mt-4 text-gray-600">Ìñ•Ïàò Î™©Î°ùÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ë...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary-50 to-secondary-50 py-12">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Ìó§Îçî */}
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">
            Ìñ•Ïàò Ïª¨Î†âÏÖò
          </h1>
          <p className="text-xl text-gray-600 max-w-2xl mx-auto">
            Îã§ÏñëÌïú Ìñ•ÏàòÎì§ÏùÑ ÎëòÎü¨Î≥¥Í≥† ÎãπÏã†ÏóêÍ≤å ÎßûÎäî Ìñ•ÏàòÎ•º Ï∞æÏïÑÎ≥¥ÏÑ∏Ïöî
          </p>
        </div>

        {/* Í≤ÄÏÉâ Î∞è ÌïÑÌÑ∞ */}
        <div className="bg-white rounded-2xl shadow-lg p-6 mb-8">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            {/* Í≤ÄÏÉâ */}
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
              <input
                type="text"
                placeholder="Ìñ•Ïàò Í≤ÄÏÉâ..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
              />
            </div>

